    public LogActivityPanel(MainFrame mainFrame, PracticeRecorder recorder) {
        this.recorder = recorder;
        this.mainFrame = mainFrame;
        practiceSessionMade = false;
        currentSession = new PracticeSession(new DateClass("0", "0", "0"));
        initComponents();
        
        /*
        Create an array to full the dropdown list from the exercises
        */
        int exerciseNumber = recorder.getTechnicalList().size();
        String exerciseList[] = new String[exerciseNumber + 1];
        exerciseList[0] = " ";
        int i = 1;
        for(TechnicalWork exercise : recorder.getTechnicalList()){
            exerciseList[i] = exercise.toString();
            i++;
        }
        resourceDropDown.setModel(new javax.swing.DefaultComboBoxModel<>(exerciseList));
    }

