    /**
     * Adds a new activity to the current practice session
     * @param evt 
     */
    private void addActivityButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addActivityButtonActionPerformed
        TechnicalWork addedExercise = new TechnicalWork("Default");
        
        for(TechnicalWork exercise : recorder.getTechnicalList()){
            if(resourceDropDown.getSelectedItem().toString().equals(exercise.getName())){
                addedExercise = exercise;
            }
        }
        if(!addedExercise.getName().equals("Default")){
            if(!practiceSessionMade){
                currentSession.setPracticeDate(new DateClass(monthInput.getSelectedItem().toString(), dayInput.getSelectedItem().toString(), yearInput.getSelectedItem().toString()));
                practiceSessionMade = true;
            }
            try{
                currentSession.addActivity(new Activity(addedExercise, Integer.parseInt(minutesPracticed.getSelectedItem().toString())));
            }
            catch(NumberFormatException ex){
                
            }
            
            DefaultTableModel model = (DefaultTableModel) activityTable.getModel();
            model.addRow(new Object[]{addedExercise.toString(), minutesPracticed.getSelectedItem().toString()});
            
            minutesPracticed.setSelectedItem(" ");
            resourceDropDown.setSelectedItem(" ");
        }
        
    }//GEN-LAST:event_addActivityButtonActionPerformed

    /**
     * Saves the currentPractice session and adds it to the list of practice sessions, exits panel
     * @param evt 
     */
    private void saveButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_saveButtonActionPerformed
        if(!currentSession.getActivityList().isEmpty()){
            recorder.addPracticeSession(currentSession);
        } 
        mainFrame.ReplacePanel(new WelcomeScreen(mainFrame, recorder));
    }//GEN-LAST:event_saveButtonActionPerformed

    /**
     * Doesn't save anything and exits panel
     * @param evt 
     */
    private void cancelButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cancelButtonActionPerformed
        mainFrame.ReplacePanel(new WelcomeScreen(mainFrame, recorder));
    }//GEN-LAST:event_cancelButtonActionPerformed


